import React, { useState, useCallback, useMemo } from 'react';
import { useDispatch } from 'react-redux';
import { addContact, fetchContacts } from '../redux/contactsSlice';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import css from './ContactForm.module.css';

const ContactForm = () => {
  const dispatch = useDispatch();

  const [name, setName] = useState('');
  const [number, setNumber] = useState('');

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleNumberChange = (e) => {
    setNumber(e.target.value);
  };

  const handleSubmit = useCallback(
    (e) => {
      e.preventDefault();

      if (!name.trim() || !number.trim()) {
        toast.error('Please fill in all fields.', {
          position: 'top-right',
          autoClose: 3000,
          hideProgressBar: true,
          closeOnClick: true,
          pauseOnHover: false,
          draggable: true,
        });
        return;
      }

      dispatch(addContact({ name, number }))
        .then(() => {
          toast.success(`${name} successfully added to the contacts list.`, {
            position: 'top-right',
            autoClose: 3000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: false,
            draggable: true,
          });
          setName('');
          setNumber('');
        })
        .catch((error) => {
          console.error('Error adding contact:', error);
        });
    },
    [dispatch, name, number]
  );

  return (
    <div className={css.containerForm}>
      <form className={css.contactForm} onSubmit={handleSubmit}>
        <p className={css.contactName}>Name</p>
        <input className={css.inputForm} type="text" name="name" value={name} onChange={handleNameChange} required />
        <p className={css.contactName}>Number</p>
        <input className={css.inputForm} type="tel" name="number" value={number} onChange={handleNumberChange} required />
        <button className={css.contactButton} type="submit">
          Add Contact
        </button>
      </form>
    </div>
  );
};

export default ContactForm;
