import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { loginUserAsync } from './authSlice';
import css from './LoginForm.module.css'

const LoginForm = () => {
  const dispatch = useDispatch();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

 const handleSubmit = (e) => {
  e.preventDefault();
  const userData = { email, password };
  dispatch(loginUserAsync(userData));
};

  return (
    <div className={css.loginContainer}>
      <h2>Login</h2>
        <form onSubmit={handleSubmit}>
        <label>email:</label>
        <input className={css.loginForm} type="email" value={email} onChange={handleEmailChange} required />
        <label>password:</label>
        <input className={css.loginForm} type="password" value={password} onChange={handlePasswordChange} required />
        <button className={css.loginButton} type="submit">Login</button>
        </form>
    </div>
  );
};

export default LoginForm;
