import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { loginUserAsync } from './authSlice';
import { Redirect } from 'react-router-dom'; 
import css from './LoginForm.module.css';

const LoginForm = () => {
  const dispatch = useDispatch();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [redirect, setRedirect] = useState(false); 

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      alert('Please fill in all fields');
      return;
    }

    const userData = {
      email: email,
      password: password,
    };

    console.log('Sending login request:', userData);

    try {
      await dispatch(loginUserAsync(userData));
      setRedirect(true); 
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  if (redirect) {
    return <Redirect to="/contacts" />; 
  }

  return (
    <div className={css.loginContainer}>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input className={css.loginInput} type="email" value={email} onChange={handleEmailChange} />
        </label>
        <label>
          Password:
          <input className={css.loginInput} type="password" value={password} onChange={handlePasswordChange} />
        </label>
        <button className={css.loginButton} type="submit">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
