import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchContacts = createAsyncThunk('contacts/fetchContacts', async (_, { getState, rejectWithValue }) => {
  const token = getState().auth.token;
  try {
    const response = await axios.get('https://connections-api.herokuapp.com/contacts', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    return rejectWithValue(error.response.data);
  }
});

export const addContact = createAsyncThunk('contacts/addContact', async (newContact, { getState, rejectWithValue }) => {
  const token = getState().auth.token;
  try {
    const response = await axios.post('https://connections-api.herokuapp.com/contacts', newContact, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    return rejectWithValue(error.response.data);
  }
});

export const deleteContact = createAsyncThunk('contacts/deleteContact', async (contactId, { getState, rejectWithValue }) => {
  const token = getState().auth.token;
  try {
    const response = await axios.delete(`https://connections-api.herokuapp.com/contacts/${contactId}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return contactId;
  } catch (error) {
    return rejectWithValue(error.response.data);
  }
});

const contactsSlice = createSlice({
  name: 'contacts',
  initialState: { items: [], status: null, error: null },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchContacts.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchContacts.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.items = action.payload;
      })
      .addCase(fetchContacts.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload;
      })
      .addCase(addContact.fulfilled, (state, action) => {
        state.items.push(action.payload);
      })
      .addCase(deleteContact.fulfilled, (state, action) => {
        state.items = state.items.filter((contact) => contact.id !== action.payload);
      });
  },
});

export default contactsSlice.reducer;
