import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { selectUser, logoutUser, selectError } from './redux/authSlice';
import css from './Navigation.module.css';

const Navigation = () => {
  const user = useSelector(selectUser);
  const invalidCredentials = useSelector(selectError);

  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutUser());
  };

  return (
    <nav className={css.navigationContainer}>
      <Link to="/">
        <button className={css.navigationButton}>Home</button>
      </Link>
      {!user ? (
        <>
          <Link to="/registration">
            <button className={css.navigationButton}>Registration</button>
          </Link>
          <Link to="/login">
            <button className={css.navigationButton} style={{ borderColor: invalidCredentials ? 'red' : 'initial' }}>
              Login
            </button>
          </Link>
        </>
      ) : (
        <>
          <Link to="/contacts">
            <button className={css.navigationButton}>Contacts</button>
          </Link>
          <button className={css.navigationButton} onClick={handleLogout}>
            Logout
          </button>
        </>
      )}
    </nav>
  );
};

export default Navigation;
