import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { selectUser, logoutUser } from './redux/authSlice';
import UserMenu from './redux/UserMenu';
import css from './Navigation.module.css';

const Navigation = () => {
  const user = useSelector(selectUser);
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutUser());
  };

  return (
    <header className={css.header}>
      <nav className={css.navigationContainer}>
        <Link to="/">
          <button className={css.navigationButton}>Главная</button>
        </Link>
        {!user ? (
          <>
            <Link to="/registration">
              <button className={css.navigationButton}>Регистрация</button>
            </Link>
            <Link to="/login">
              <button className={css.navigationButton}>Вход</button>
            </Link>
          </>
        ) : (
          <>
            <Link to="/contacts">
              <button className={css.navigationButton}>Контакты</button>
            </Link>
            <button className={css.navigationButton} onClick={handleLogout}>
              Выйти
            </button>
          </>
        )}
      </nav>
    </header>
  );
};

export default Navigation;
