import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { selectUser } from './redux/authSlice';
import css from './Navigation.module.css';

const Navigation = () => {
  const user = useSelector(selectUser);
  const navigate = useNavigate();

  return (
    <nav className={css.navigationContainer}>
      <Link to="/"><button className={css.navigationButton}>Home</button></Link>
      {!user ? (
        <>
          <Link to="/registration"><button className={css.navigationButton}>Registration</button></Link>
          <Link to="/login"><button className={css.navigationButton}>Login</button></Link>
        </>
      ) : (
        <Link to="/contacts"><button className={css.navigationButton}>Contacts</button></Link>
      )}
    </nav>
  );
};

export default Navigation;
