import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { loginUserAsync } from './redux/authSlice';
import css from './redux/LoginForm.module.css'; // Используйте ваш путь к стилям

const LoginForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [userData, setUserData] = useState({
    email: '',
    password: '',
  });

  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      await dispatch(loginUserAsync(userData));
      navigate('/contacts');
    } catch (error) {
      console.error('Error during login:', error);
      setError(error.response?.data?.message || 'Произошла ошибка входа. Пожалуйста, попробуйте снова.');
    }
  };

  return (
    <div className={css.loginContainer}>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form>
        <label>
          Email
          <input type="email" name="email" value={userData.email} onChange={handleChange} />
        </label>
        <label>
          Password
          <input type="password" name="password" value={userData.password} onChange={handleChange} />
        </label>
        <button type="button" className={css.yourButtonClass} onClick={handleSubmit}>Login</button>
      </form>
    </div>
  );
};

export default LoginForm;
