import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { registerUserAsync } from './authSlice';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import css from './RegisterForm.module.css';
import { useSelector } from 'react-redux';
import { selectUser } from './authSlice';

const RegisterForm = () => {
  const dispatch = useDispatch();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const user = useSelector(selectUser);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !email || !password) {
      alert('Please fill in all fields');
      return;
    }

    const userData = {
      name: name,
      email: email,
      password: password,
    };

    try {
      await dispatch(registerUserAsync(userData));
      toast.success('Registration successful!');
    } catch (error) {
      console.error('Error during registration:', error);
    }
  };

  return (
    <div className={css.registerContainer}>
      <h2>Registration</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input className={css.registerInput} type="text" value={name} onChange={handleNameChange} />
        </label>
        <label>
          Email:
          <input className={css.registerInput} type="email" value={email} onChange={handleEmailChange} />
        </label>
        <label>
          Password:
          <input className={css.registerInput} type="password" value={password} onChange={handlePasswordChange} />
        </label>
        <button className={css.registerButton} type="submit">
          Register
        </button>
      </form>
    </div>
  );
};

export default RegisterForm;
