import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { loginUserAsync, selectUser } from './authSlice';
import css from './LoginForm.module.css';

const LoginForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const user = useSelector(selectUser);

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    setError(null);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setError(null); 
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError('Please fill in all fields');
      return;
    }

    const userData = {
      email: email,
      password: password,
    };

    try {
      const response = await dispatch(loginUserAsync(userData));

      if (response.payload.user) {
        navigate('/contacts');
      } else {
        setError('Invalid email or password'); 
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('Invalid email or password');
    }
  };

  return (
    <div className={css.loginContainer}>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input className={css.loginInput} type="email" value={email} onChange={handleEmailChange} />
        </label>
        <label>
          Password:
          <input className={css.loginInput} type="password" value={password} onChange={handlePasswordChange} />
        </label>
        <button className={css.loginButton} type="submit">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
