import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { selectUser, logoutUser } from '../redux/authSlice';
import css from './Navigation.module.css';

const NavigationButton = ({ to, text, onClick }) => (
  <Link to={to}>
    <button className={css.navigationButton} onClick={onClick}>
      {text}
    </button>
  </Link>
);

const Navigation = () => {
  const user = useSelector(selectUser);
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutUser());
  };

  const buttons = !user
    ? [
        { to: '/registration', text: 'Registration' },
        { to: '/login', text: 'Login' },
      ]
    : [
        { to: '/contacts', text: 'Contacts' },
        { text: 'Logout', onClick: handleLogout },
      ];

  return (
    <nav className={css.navigationContainer}>
      <Link to="/">
        <button className={css.navigationButton}>Home</button>
      </Link>
      {buttons.map((button, index) => (
        <NavigationButton key={index} {...button} />
      ))}
    </nav>
  );
};

export default Navigation;
