import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { selectUser, logoutUser } from './redux/authSlice';
import css from './Navigation.module.css';

const Navigation = () => {
  const user = useSelector(selectUser);
  const dispatch = useDispatch();
  const location = useLocation();

  const handleLogout = () => {
    dispatch(logoutUser());
  };

  return (
    <nav className={css.navigationContainer}>
      <Link to="/">
        <button className={css.navigationButton}>Home</button>
      </Link>
      {!user ? (
        <>
          <Link to="/registration">
            <button className={css.navigationButton}>Registration</button>
          </Link>
          <Link to="/login">
            <button className={css.navigationButton}>Login</button>
          </Link>
        </>
      ) : location.pathname === '/contacts' && (
        <button className={css.navigationButton} onClick={handleLogout}>
          Logout
        </button>
      )}
    </nav>
  );
};

export default Navigation;
