import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { registerUserAsync } from './authSlice';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const RegisterForm = () => {
  const dispatch = useDispatch();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      
      await dispatch(registerUserAsync({ name, email, password }));

     
      toast.success(`${name}, ви успішно зареєстровані!`, {
        position: 'top-right',
        autoClose: 3000,
        hideProgressBar: true,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
      });

      
      setName('');
      setEmail('');
      setPassword('');
    } catch (error) {
     
      console.error('Помилка при реєстрації:', error);
      toast.error('Помилка при реєстрації. Будь ласка, спробуйте ще раз.', {
        position: 'top-right',
        autoClose: 3000,
        hideProgressBar: true,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
      });
    }
  };

  return (
    <div>
      <for onSubmit={handleSubmit}>
        <p>Name</p>
        <input type="text" name="name" value={name} onChange={handleNameChange} required />
        <p>Email</p>
        <input type="email" name="email" value={email} onChange={handleEmailChange} required />
        <p>Password</p>
        <input
          type="password"
          name="password"
          value={password}
          onChange={handlePasswordChange}
          required
        />
        <button type="submit">
          Register
        </button>
      </for>
    </div>
  );
};

export default RegisterForm;
