import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { loginUserAsync } from './authSlice';
import { useNavigate } from 'react-router-dom';
import css from './LoginForm.module.css';

const LoginForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      alert('Будь ласка, заповніть всі поля');
      return;
    }

    try {
      await dispatch(loginUserAsync({ email, password }));
      // Перенаправлення на сторінку контактів після успішного входу
      navigate('/contacts');
    } catch (error) {
      console.error('Помилка під час входу:', error);
    }
  };

  return (
    <div className={css.loginContainer}>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>Email:</label>
        <input className={css.loginForm} type="email" value={email} onChange={handleEmailChange} required />
        <label>Password:</label>
        <input className={css.loginForm} type="password" value={password} onChange={handlePasswordChange} required />
        <button className={css.loginButton} type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginForm;
