import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export const selectUser = (state) => state.auth.user;

const baseUrl = 'https://connections-api.herokuapp.com';

export const fetchContacts = createAsyncThunk('contacts/fetchAll', async (_, thunkAPI) => {
  const user = selectUser(thunkAPI.getState());

  if (!user) {
    return [];
  }

  try {
    const response = await axios.get(`${baseUrl}/contacts`, {
      headers: {
        Authorization: `Bearer ${user.token}`,
      },
    });
    return response.data;
  } catch (error) {
    throw error;
  }
});

export const addContact = createAsyncThunk('contacts/add', async (contactData, thunkAPI) => {
  const user = selectUser(thunkAPI.getState());

  if (!user) {
    return [];
  }

  try {
    const response = await axios.post(`${baseUrl}/contacts`, contactData, {
      headers: {
        Authorization: `Bearer ${user.token}`,
      },
    });
    toast.success('Contact added successfully');
    return response.data;
  } catch (error) {
    toast.error('Error adding contact');
    throw error;
  }
});

export const deleteContact = createAsyncThunk('contacts/delete', async (contactId, thunkAPI) => {
  const user = selectUser(thunkAPI.getState());

  if (!user) {
    return [];
  }

  try {
    await axios.delete(`${baseUrl}/contacts/${contactId}`, {
      headers: {
        Authorization: `Bearer ${user.token}`,
      },
    });
    toast.success('Contact deleted successfully');
    return contactId;
  } catch (error) {
    toast.error('Error deleting contact');
    throw error;
  }
});

export const contactsSlice = createSlice({
  name: 'contacts',
  initialState: {
    items: [],
    status: 'idle',
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchContacts.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchContacts.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.items = action.payload;
      })
      .addCase(fetchContacts.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(addContact.fulfilled, (state, action) => {
        state.items.push(action.payload);
      })
      .addCase(deleteContact.fulfilled, (state, action) => {
        state.items = state.items.filter((contact) => contact.id !== action.payload);
      });
  },
});

export default contactsSlice.reducer;
