import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { selectIsLoggedIn, logout } from './redux/authSlice';
import UserMenu from './redux/UserMenu';
import css from './Navigation.module.css';

const Navigation = () => {
  const isLoggedIn = useSelector(selectIsLoggedIn);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleLogout = () => {

    dispatch(logout());

    navigate('/');
  };

  return (
    <header className={css.header}>
      <nav className={css.navigationContainer}>

        <Link to="/">
          <button className={css.navigationButton}>Главная</button>
        </Link>
        {!isLoggedIn ? (
          <>
            <Link to="/registration">
              <button className={css.navigationButton}>Регистрация</button>
            </Link>
            <Link to="/login">
              <button className={css.navigationButton}>Вход</button>
            </Link>
          </>
        ) : (
          <>
            <Link to="/contacts">
              <button className={css.navigationButton}>Контакты</button>
            </Link>

            <button className={css.navigationButton} onClick={handleLogout}>
              Выход
            </button>
          </>
        )}
        <UserMenu />
      </nav>
    </header>
  );
};

export default Navigation;
